targets.expr:
sum(phpfpm_active_processes{cluster=~"$cluster", namespace=~"$namespace", service=~"$service", pool=~"$pool"}
) by(pool)
  /
sum( phpfpm_total_processes{cluster=~"$cluster", namespace=~"$namespace", service=~"$service", pool=~"$pool"}
) by(pool) * 100

sum(
  irate(
    container_cpu_usage_seconds_total{pod=~"$service-.*",container=~"$container", namespace=~"$namespace"}[1m]
  )
) by (namespace,container,pod) /
sum(
  container_spec_cpu_quota{pod=~"$service-.*",container=~"$container", namespace=~"$namespace"} /
  container_spec_cpu_period{pod=~"$service-.*",container=~"$container", namespace=~"$namespace"}
) by (namespace,container,pod)
